INSERT INTO `sort` VALUES (1, '可回收垃圾', '可回收物(再生资源)是指回收后经过再加工可以成为生产原料或者经过整理可以再利用的物品，主要包括废纸类、塑料类、玻璃类、金属类、电子废弃物类、织物类等。', '2023-04-04 22:27:36', '2023-04-25 17:19:40', '可回收垃圾');
INSERT INTO `sort` VALUES (2, '有害垃圾', '有毒有害垃圾是指存有对人体健康有害的重金属、有毒的物质或者对环境造成现实危害或者潜在危害的废弃物。', '2023-04-25 17:09:49', '2023-05-02 22:45:44', NULL);
INSERT INTO `sort` VALUES (3, '厨余垃圾', '狭义的厨余垃圾是有机垃圾的一种，分为熟厨余包括剩菜、剩饭、菜叶；生厨余垃圾包括果皮、蛋壳、茶渣、骨、贝壳；泛指家庭生活饮食中所需用的来源生料及成品(熟食)或残留物。但广义的厨余垃圾还包括用过的筷子，食品的包装材料等。', '2023-05-02 22:44:41', NULL, NULL);
INSERT INTO `sort` VALUES (4, '其他垃圾', '包括除上述几类垃圾之外难以回收的废弃物，通常根据垃圾特性采取焚烧或者填埋的方式处理。', '2023-05-02 22:46:23', '2023-05-02 23:13:12', NULL);
